  //       UserStore.loading = false;
  //       UserStore.isLoggedIn = false;
  //     }
  //   }
  //   catch (e) {
  //     UserStore.loading = false;
  //     UserStore.isLoggedIn = false;
  //   }
  // }

  // // ===DESTROY SESSION===
  // async doLogout() {
  //   try {
  //     let res = await fetch('/logout', {
  //       method: 'post',
  //       headers: {
  //         'Accept': 'application/json',
