  //     })
  //     let result = await res.json();
  //     if (result && result.success) {
  //       UserStore.isLoggedIn = false;
  //       UserStore.email = '';
  //     }

  //   }
  //   catch (e) {
  //     console.log(e)
  //   }
  // }

  render() {

    if (UserStore.loading) {
      return (
        <div>
          <div className="App">
            <div className="container">
              loading, please wait...
            </div>
          </div>
        </div>
      )
    } else {

      // === USER IS LOGGED IN===
      if (UserStore.isLoggedIn) {
        return (
          <HashRouter>
            <div className="App">
              <div>
                <Main />
                <nav className="nav-bar">
                  <div className="logo-container">
                    <img className="logo" src="hand.png" alt="" />
                    <h1 className="site-name">ulture Club</h1>
                  </div>
                  <ul className="menu">
                    <LogoutButton onClick={() => this.doLogout()} />
                    <span className="menu-division">|</span>
                    <li><NavLink className="session-btns signup-session-btn" to="/profile">profile</NavLink></li>
                    <NavLink className="home-session-btn hvr-radial-out" exact to="/"><i className="fas fa-bars"></i></NavLink>
                  </ul>
                </nav>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
                  <Route path="/signup" component={Profile} />
                </div>
              </div>
            </div>
          </HashRouter>
        )

        // === USER IS NOT LOGGED IN===
      } else {
        return (
          <HashRouter>
            <div className="App">
              <div>
                <Main />
                <nav className="nav-bar">
                  <div className="logo-container">
                    <img className="logo" src="hand.png" alt="" />
                    <h1 className="site-name">ulture Club</h1>
                  </div>
                  <ul className="menu">
                    <li><NavLink className="session-btns login-session-btn" to="/login">login</NavLink></li>
                    <span className="menu-division">|</span>
                    <li><NavLink className="session-btns signup-session-btn" to="/signup">signup</NavLink></li>
                    <li><NavLink className="home-session-btn hvr-radial-out" exact to="/"><i className="fas fa-bars"></i></NavLink></li>
                  </ul>
                </nav>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
                  <Route path="/signup" component={SignUp} />
                </div>
              </div>
            </div>
          </HashRouter>
        );
      }
    }
  }
}

export default observer(App);
