import React, { Component } from "react";
import EventStore from '../../stores/EventStore'
import axios from 'axios'
import './Events.css'
import queryString from 'query-string'
import {
    Route,
    NavLink,
    HashRouter
} from "react-router-dom";

class Events extends Component {

    state = {
        eventSession: false
    }

    componentDidMount() {
        if (EventStore.accessToken === '') {
            let parsed = queryString.parse(window.location.search);
            let accessToken = parsed.access_token
            EventStore.accessToken = accessToken
            window.history.replaceState({}, document.title, "/");
        }
        if (EventStore.accessToken !== undefined) {
            this.setState({
                eventSession: true
            })
            this.fetchEvents()
        }
    }
    fetchEvents = () => {

        let config = {
            method: 'get',
            url: 'https://www.eventbriteapi.com/v3/categories/103/',
            headers: {
                'Authorization': `Bearer ${EventStore.accessToken}`
            }
        };

        axios(config)
            .then(function (response) {
                console.log(JSON.stringify(response.data));
            })
            .catch(function (error) {
                // console.log(error);
            });
    }

    render() {
        console.log(this.state.eventSession)
        if (!this.state.eventSession) {
            return (
                <div >
                    <h2>Events</h2>
                    <button onClick={() => window.location = 'http://localhost:8888/getEvents'}>Check for Events</button>
                </div >
            );
        } else {
            return (
                <div >
                    <h2>Events</h2>
                    <p>Here are your events</p>
                </div >
            );
        }
    }
}

export default Events;

// https://www.eventbrite.com/v3?events/search/?token=ILROY7WB7IH75CCRBEQI&expand=venue&location.within=10km&location.address=london&sort_by=distance&categories=103

