import React from 'react';
import './Email.css'

function Email(props) {
    var style = {
        color: 'white',
        fontSize: 200
    };
    return (
        < div className="email-container" >
            <input
                validemail={props.validemail}
                onChange={props.onChange}
                value={props.email}
                placeholder="Email"
                type="Email"
            />
            <p >{`${props.validemail === "false"
                ? "Required"
                : "Checked"}`}
            </p>
        </div>
    );
}

export default Email;

