import React, { Component } from "react";
import Main from "./components/main/Main";
import axios from "axios";
import { observer } from "mobx-react";
import UserStore from "./stores/UserStore";
import EventStore from "./stores/EventStore";
import Login from "./components/login/Login";
import SignUp from "./components/signup/Signup";
import Profile from "./components/Profile/Profile";
import LogoutButton from "./components/LogoutButton";
import Home from "./components/Home";
import { Route, NavLink, HashRouter } from "react-router-dom";

import "./App.css";
import Events from "./components/events/Events";

class App extends Component {
  // === CHECK FOR SESSION ===

  componentDidMount() {
    let config = {
      method: "post",
      url: "http://localhost:8888/isLoggedIn",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    };
    axios(config)
      .then((res) => {
        console.log(res.data);
        if (res && res.success) {
          UserStore.isLoggedIn = true;
          UserStore.userID = res.userID;
          UserStore.username = res.username;
        } else {
          UserStore.isLoggedIn = false;
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  // ===DESTROY SESSION===

  doLogout() {
    let config = {
      method: "post",
      url: "http://localhost:8888/logout",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    };
    axios(config)
      .then((res) => {
        res = res.data;
        console.log(`log out res: ${res}`);
        if (res && res.success) {
          UserStore.isLoggedIn = true;
          UserStore.username = "";
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  render() {
    // === USER IS LOGGED IN===
    console.log(UserStore);
    if (UserStore.isLoggedIn) {
      console.log(UserStore);
      return (
        <HashRouter>
          <div className="App">
            <div>
              <Main />
              <nav className="nav-bar">
                <div className="logo-container">
                  <img className="logo" src="hand.png" alt="" />
                  <h1 className="site-name">ulture Club</h1>
                </div>
                <ul className="menu">
                  <LogoutButton onClick={() => this.doLogout()} />
                  <span className="menu-division">|</span>
                  <li>
                    <NavLink
                      className="session-btns signup-session-btn"
                      to="/profile"
                    >
                      profile
                    </NavLink>
                  </li>
                  <NavLink
                    className="home-session-btn hvr-radial-out"
                    exact
                    to="/"
                  >
                    <i className="fas fa-bars"></i>
                  </NavLink>
                </ul>
              </nav>
              <div className="content">
                <Route exact path="/" component={Home} />
                <Route path="/login" component={Login} />
                <Route path="/profile" component={Profile} />
              </div>
            </div>
          </div>
        </HashRouter>
      );

      // === USER IS NOT LOGGED IN===
    } else {
      return (
        <HashRouter>
          <div className="App">
            <div>
              <Main />
              <nav className="nav-bar">
                <div className="logo-container">
                  <img className="logo" src="hand.png" alt="" />
                  <h1 className="site-name">ulture Club</h1>
                </div>
                <ul className="menu">
                  <li>
                    <NavLink
                      className="session-btns login-session-btn"
                      to="/login"
                    >
                      login
                    </NavLink>
                  </li>
                  <span className="menu-division">|</span>
                  <li>
                    <NavLink
                      className="session-btns signup-session-btn"
                      to="/signup"
                    >
                      signup
                    </NavLink>
                  </li>
                  <li>
                    <NavLink
                      className="home-session-btn hvr-radial-out"
                      exact
                      to="/"
                    >
                      <i className="fas fa-bars"></i>
                    </NavLink>
                  </li>
                </ul>
              </nav>
              <div className="content">
                <Route exact path="/" component={Home} />
                <Route path="/login" component={Login} />
                <Route path="/signup" component={SignUp} />
              </div>
            </div>
          </div>
        </HashRouter>
      );
    }
  }
}

export default observer(App);
