import React, { Component } from "react";
import Main from "./components/main/Main";
import { observer } from "mobx-react";
import UserStore from "./stores/UserStore";
import axios from "axios";
import Login from "./components/login/Login";
import SignUp from "./components/signup/Signup";
import Profile from "./components/Profile/Profile";
import LogoutButton from "./components/LogoutButton";
import Home from "./components/Home";
import { Route, NavLink, HashRouter } from "react-router-dom";

import "./App.css";

class App extends Component {
  // === CHECK FOR SESSION ===

  componentDidMount() {
    let config = {
      method: "post",
      url: "http://localhost:8888/isLoggedIn",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    };
    axios
      .post("http://localhost:8888/isLoggedIn")
      .then((res) => {
        // res = res.data;
        // console.log(res);
        if (res && res.success) {
          UserStore.isLoggedIn = true;
          UserStore.userID = res.userID;
          UserStore.username = res.username;
        } else {
          UserStore.isLoggedIn = false;
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  // ===DESTROY SESSION===

  doLogout() {
    let config = {
      method: "post",
      url: "http://localhost:8888/logout",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    };
    axios(config)
      .then((res) => {
        res = res.data;
        console.log(res);
        if (res && res.success) {
          UserStore.isLoggedIn = true;
          UserStore.isLoggedIn = false;
          UserStore.username = "";
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  // async doLogout() {
  //   try {
  //     let res = await fetch("http://localhost:8888/logout", {
  //       method: "post",
  //       headers: {
  //         Accept: "application/json",
  //         "Content-Type": "application/json",
  //       },
  //     });
  //     let result = await res.json();
  //     if (result && result.success) {
  //       UserStore.isLoggedIn = false;
  //       UserStore.username = "";
  //     }
  //   } catch (e) {
  //     console.log(e);
  //   }
  // }

  render() {
    if (UserStore.loading) {
      return (
        <div>
          <div className="App">
            <div className="container">loading, please wait...</div>
          </div>
        </div>
      );
    } else {
      // === USER IS LOGGED IN===
      if (UserStore.isLoggedIn) {
        return (
          <HashRouter>
            <div className="App">
              <div>
                <Main />
                <nav className="nav-bar">
                  <div className="logo-container">
                    <img className="logo" src="hand.png" alt="" />
                    <h1 className="site-name">ulture Club</h1>
                  </div>
                  <ul className="menu">
                    <LogoutButton onClick={() => this.doLogout()} />
                    <span className="menu-division">|</span>
                    <li>
                      <NavLink
                        className="session-btns signup-session-btn"
                        to="/profile"
                      >
                        profile
                      </NavLink>
                    </li>
                    <NavLink
                      className="home-session-btn hvr-radial-out"
                      exact
                      to="/"
                    >
                      <i className="fas fa-bars"></i>
                    </NavLink>
                  </ul>
                </nav>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
                  <Route path="/profile" component={Profile} />
                </div>
              </div>
            </div>
          </HashRouter>
        );

        // === USER IS NOT LOGGED IN===
      } else {
        return (
          <HashRouter>
            <div className="App">
              <div>
                <Main />
                <nav className="nav-bar">
                  <div className="logo-container">
                    <img className="logo" src="hand.png" alt="" />
                    <h1 className="site-name">ulture Club</h1>
                  </div>
                  <ul className="menu">
                    <li>
                      <NavLink
                        className="session-btns login-session-btn"
                        to="/login"
                      >
                        login
                      </NavLink>
                    </li>
                    <span className="menu-division">|</span>
                    <li>
                      <NavLink
                        className="session-btns signup-session-btn"
                        to="/signup"
                      >
                        signup
                      </NavLink>
                    </li>
                    <li>
                      <NavLink
                        className="home-session-btn hvr-radial-out"
                        exact
                        to="/"
                      >
                        <i className="fas fa-bars"></i>
                      </NavLink>
                    </li>
                  </ul>
                </nav>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
                  <Route path="/signup" component={SignUp} />
                </div>
              </div>
            </div>
          </HashRouter>
        );
      }
    }
  }
}

export default observer(App);
