import React, { Component } from 'react';
import Password from './Password'
import Email from './Email'
import './Signup.css'


class Signup extends Component {
    constructor(props) {
        super(props);
    }
    state = {
        email: '',
        password: '',
        isToggleOn: true
    }
    handleToggle = () => {
        this.setState(state => ({
            isToggleOn: !state.isToggleOn
        }));
    };
    handlePassword = e => {
        this.setState({
            password: e.target.value
        })
    }
    handleEmail = e => {
        this.setState({
            email: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault()
    }
    render() {
        const { email, password, isLoggedIn, isToggleOn } = this.state

        return (
            <div className="sign-up-form">
                <form onSubmit={this.handleSubmit}>
                    <h2>{!isLoggedIn ? "Sign up" : "Log in"}</h2>
                    <Email name="email" email={email} onChange={this.handleEmail} />
                    <Password name="password" handleToggle={this.handleToggle} isToggleOn={isToggleOn} password={password} onChange={this.handlePassword} />
                </form>
            </div>
        );
    }
}

export default Signup;



