import React, { Component } from "react";
import axios from "axios";
import "./Events.css";
import { Route, NavLink, HashRouter } from "react-router-dom";

class Events extends Component {
  state = {
    // eventSession: false,
    data: [],
  };

  //   async fetchData() {
  //     try {
  //       let res = await fetch("getEvents", {
  //         method: "get",
  //       });

  //       let result = await res.json();
  //       this.setState({
  //         data: [result._embedded.events, ...this.state.ingredients],
  //       });
  //     } catch (e) {
  //       console.log(e);
  //     }
  //   }

  async fetchData() {
    axios
      .get(
        `https://app.ticketmaster.com/discovery/v2/events.json?city=melbourne&page=3&size=20&apikey=ARzROEpcmT85JLMu1NVGUCSSgS8LQDGd`
      )
      .then((res) => this.setState({ data: res.data._embedded.events }));
  }
  renderEvents = (e, idx) => {
    console.log(e);
    return (
      <div key="idx">
        <img className="event-img" src={e.images[1].url} alt="" />
        <p>{e.name}</p>
        <p>{e._embedded.venues[0].name}</p>
        <p>{e._embedded.venues[0].address.line1}</p>
      </div>
    );
  };

  render() {
    const { data } = this.state;
    if (data.length !== 0) {
      let events = data.filter((val, id, array) => array.indexOf(val) == id);
      return (
        <div>
          <h2>Events</h2>
          <section className="events">{events.map(this.renderEvents)}</section>
          <button onClick={() => this.fetchData()}>Check for Events</button>
        </div>
      );
    } else {
      return (
        <div>
          <h2>Events</h2>
          <button onClick={() => this.fetchData()}>Check for Events</button>
        </div>
      );
    }
  }
}

export default Events;
