import React, { Component } from 'react';
import Main from './components/main/Main'
import { observer } from 'mobx-react';
import UserStore from './stores/UserStore'
import LoginForm from './components/login/Login'
import SignUp from './components/signup/Signup'
import LogoutButton from './components/LogoutButton'


import './App.css';

class App extends Component {

  // ===CHECK FOR SESSION===

  async componentDidMount() {
    try {
      let res = await fetch('/isLoggedIn', {
        method: 'post',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      })
      let result = await res.json();
      if (result && result.success) {
        UserStore.loading = false;
        UserStore.isLoggedIn = true;
        UserStore.email = result.email;
      } else {
        UserStore.loading = false;
        UserStore.isLoggedIn = false;
      }
    }
    catch (e) {
      UserStore.loading = false;
      UserStore.isLoggedIn = false;
    }
  }

  // ===DESTROY SESSION===
  async doLogout() {
    try {
      let res = await fetch('/logout', {
        method: 'post',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      })
      let result = await res.json();
      if (result && result.success) {
        UserStore.isLoggedIn = false;
        UserStore.email = '';
      }

    }
    catch (e) {
      console.log(e)
    }
  }

  render() {

    // =-------------Use the router instead!!!

    if (UserStore.loading) {
      return (
        <div>
          <div className="App">
            <div className="container">
              loading, please wait...
            </div>
          </div>
        </div>
      )
    } else {

      // === USER IS LOGGED IN===
      if (UserStore.isLoggedIn) {
        return (
          <HashRouter>
            <div className="Main">
              <div className="img-banner">
                <img className="img-main" src="https://images.unsplash.com/photo-1464820453369-31d2c0b651af?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1000&q=80" alt="" />
              </div>
              <h1 className="site-name">Culture Club</h1>
              <ul className="header">
                <li><NavLink exact to="/">Home</NavLink></li>
                <li><NavLink to="/logout">Logout</NavLink></li>
                <li><NavLink to="/events">Events</NavLink></li>
              </ul>
              <div className="content">
                <Route exact path="/" component={Home} />
                <Route path="/logout" component={LogoutButton} />
                <Route path="/events" component={Events} />
              </div>
            </div>
          </HashRouter>
          // );
          // <div>
          //   <div className="App">
          //     <div className="container">

          //       <h2>Logged in</h2>
          //       <LogoutButton
          //         onClick={() => this.doLogout()}
          //       />
          //     </div>
          //   </div>
          // </div>
        )

        // === USER IS NOT LOGGED IN===
      } else {
        return (
          <div className="App">
            {/* <div className="container">
              <LoginForm />
            </div> */}

            <div>
              <Main />
            </div>
          </div>
        );
      }
    }
  }
}

export default observer(App);
