import React, { Component } from "react";
import Email from '../signup/Email'
import LoginPassword from './LoginPassword'
import SubmitButton from "./SubmitButton";
import UserStore from "../../stores/UserStore";
import "./Login.css";
import {
  Route,
  Link,
  HashRouter
} from "react-router-dom";

class Login extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      buttonDisabled: false,
      isToggleOn: false
    }
  }
  handleToggle = () => {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
  };
  handlePassword = e => {
    this.setState({
      password: e.target.value
    })
  }
  handleEmail = e => {
    this.setState({
      email: e.target.value
    })
  }

  async handleLogin() {
    if (!this.state.email) {
      return;
    }
    if (!this.state.password) {
      return;
    }
    this.setState({
      buttonDisabled: true,
    });
    try {
      let res = await fetch("login", {
        method: "post",
        headers: {
          Accept: "application/json",
          "Content-type": "application/json",
        },
        body: JSON.stringify({
          email: this.state.email,
          password: this.state.password,
        }),
      });

      let result = await res.json();

      // ===LOGIN SUCCESSFUL===
      if (result && result.success) {
        UserStore.isLoggedIn = true;
        UserStore.email = result.email;


        // ===LOGIN FAILED===
      } else if (result && result.success === false) {
        this.resetForm();
        alert(result.msg);
      }
    } catch (e) {
      console.log(e);
      this.resetForm();
    }
  }

  resetForm() {
    this.setState({
      email: "",
      password: "",
      buttonDisabled: false,
    });
  }

  render() {
    const { email, password, buttonDisabled, isToggleOn } = this.state
    if (UserStore.isLoggedIn) {
      return (
        <HashRouter>
          <div className="login-container">
            <div className="login-form">
              <h2>Profie</h2>
              <Link className="" exact to="/">Home</Link>
              <div className="content">
                <Route exact path="/" component={Home} />
              </div>
            </div>
          </div >
        </HashRouter >
      )
    } else {
      return (
        <div className="login-container">
          <div className="login-form">
            <div className="img-banner">
              <img className="img-main" src="boat.png" alt="" />
            </div>
            <form>
              <h2>Log in</h2>
              <Email name="email" email={email} onChange={this.handleEmail} />
              <LoginPassword name="password" handleToggle={this.handleToggle} isToggleOn={isToggleOn} password={password} onChange={this.handlePassword} />
              <SubmitButton
                disabled={buttonDisabled}
                onClick={() => this.handleLogin()}
              />
            </form>
          </div >
        </div>
      );
    }
  }
}

export default Login;
