        </div>
      )
    } else {

      // === USER IS LOGGED IN===
      if (UserStore.isLoggedIn) {
        return (
          <HashRouter>
            <div className="App">
              <h1 className="site-name">Culture Club</h1>
              <img className="logo" src="boat.png" alt="" />
              <div>
                <Main />
                <ul className="header">
                  <NavLink className="home-session-btn" exact to="/"><i class="fas fa-bars"></i></NavLink>
                  <LogoutButton
                    onClick={() => this.doLogout()}
                  />
                </ul>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
