import React, { Component } from 'react';
import './Email.css'

class Email extends Component {
    state = {
        email: ''
    }
    handleChange = e => {
        this.setState({
            email: e.target.value
        })
    }
    render() {
        const email = this.state.email
        return (
            <div className="email-container">
                <input
                    onChange={this.handleChange}
                    value={email}
                    placeholder="Email"
                    type="Email"
                />
            </div>
        );
    }
}

export default Email;
