import React, { Component } from "react";
import Email from '../signup/Email'
import InputField from "./InputField";
import SubmitButton from "./SubmitButton";
import UserStore from "../../stores/UserStore";
import "./Login.css";

class LoginForm extends Component {

  state = {
    email: '',
    password: '',
    buttonDisabled: false
  }

  // handlePassword = e => {
  //   this.setState({
  //     password: e.target.value
  //   })
  // }
  handleEmail = e => {
    this.setState({
      email: e.target.value
    })
  }
  // handleSubmit = (e) => {
  //   e.preventDefault()
  // }


  setInputValue = (property, val) => {
    val = val.trim();
    if (val.length > 50) {
      return;
    }
    this.setState({
      [property]: val,
    });
  }

  async doLogin() {
    if (!this.state.email) {
      return;
    }
    if (!this.state.password) {
      return;
    }
    this.setState({
      buttonDisabled: true,
    });
    try {
      let res = await fetch("login", {
        method: "post",
        headers: {
          Accept: "application/json",
          "Content-type": "application/json",
        },
        body: JSON.stringify({
          email: this.state.email,
          password: this.state.password,
        }),
      });

      let result = await res.json();
      if (result && result.success) {
        UserStore.isLoggedIn = true;
        UserStore.email = result.email;
        console.log(UserStore.email)
      } else if (result && result.success === false) {
        this.resetForm();
        alert(result.msg);
      }
    } catch (e) {
      console.log(e);
      this.resetForm();
    }
  }

  resetForm() {
    this.setState({
      email: "",
      password: "",
      buttonDisabled: false,
    });
  }

  render() {
    const { email, password, buttonDisabled } = this.state
    return (
      <div className="loginForm">
        Log in
        <Email value={this.state.email ? this.state.email : ""} name="email" email={email} onChange={this.handleEmail} />
        <Password name="password" handleToggle={this.handleToggle} isToggleOn={isToggleOn} password={password} onChange={this.handlePassword} />
        {/* <InputField
          type="text"
          placeholder="Email"
          value={this.state.email ? this.state.email : ""}
          onChange={(val) => this.setInputValue("email", val)}
        /> */}

        <InputField
          type="password"
          placeholder="Password"
          value={this.state.password ? this.state.password : ""}
          onChange={(val) => this.setInputValue("password", val)}
        />
        <SubmitButton
          text="Login"
          disabled={this.state.buttonDisabled}
          onClick={() => this.doLogin()}
        />
      </div>
    );
  }
}

export default LoginForm;
