import React, { Component } from 'react';
import Main from './components/main/Main'
import { observer } from 'mobx-react';
import UserStore from './stores/UserStore'
import Login from './components/login/Login'
import SignUp from './components/signup/Signup'
import LogoutButton from './components/LogoutButton'
import Home from "./components/Home";
import {
  Route,
  NavLink,
  HashRouter
} from "react-router-dom";

import './App.css';

class App extends Component {

  // === CHECK FOR SESSION ===

  async componentDidMount() {
    try {
      let res = await fetch('/isLoggedIn', {
        method: 'post',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      })
      let result = await res.json();
      if (result && result.success) {
        UserStore.loading = false;
        UserStore.isLoggedIn = true;
        UserStore.email = result.email;
      } else {
        UserStore.loading = false;
        UserStore.isLoggedIn = false;
      }
    }
    catch (e) {
      UserStore.loading = false;
      UserStore.isLoggedIn = false;
    }
  }

  // ===DESTROY SESSION===
  async doLogout() {
    try {
      let res = await fetch('/logout', {
        method: 'post',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      })
      let result = await res.json();
      if (result && result.success) {
        UserStore.isLoggedIn = false;
        UserStore.email = '';
      }

    }
    catch (e) {
      console.log(e)
    }
  }

  render() {

    if (UserStore.loading) {
      return (
        <div>
          <div className="App">
            <div className="container">
              loading, please wait...
            </div>
          </div>
        </div>
      )
    } else {

      // === USER IS LOGGED IN===
      if (UserStore.isLoggedIn) {
        return (
          <HashRouter>
            <div className="App">
              <div>
                <Main />
                <nav className="nav-bar">
                  <div class="logo-container">
                    <img className="logo" src="hand.png" alt="" />
                    <h1 className="site-name">ulture Club</h1>
                  </div>
                  <ul className="menu">
                    <LogoutButton onClick={() => this.doLogout()} />
                    <NavLink className="home-session-btn hvr-radial-out" exact to="/"><i class="fas fa-bars"></i></NavLink>
                  </ul>
                </nav>
                <nav className="nav-bar">
                  <h1 className="site-name">Culture Club</h1>
                  <img className="logo" src="hand.png" alt="" />

                </nav>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
                  <Route path="/signup" component={SignUp} />
                </div>
              </div>
            </div>
          </HashRouter>
        )

        // === USER IS NOT LOGGED IN===
      } else {
        return (
          <HashRouter>
            <div className="App">

              <div>
                <Main />
                <nav className="nav-bar">
                  <div class="logo-container">
                    <img className="logo" src="hand.png" alt="" />
                    <h1 className="site-name">ulture Club</h1>
                  </div>
                  <ul className="menu">
                    <li><NavLink className="session-btns login-session-btn" to="/login">login</NavLink></li>
                    <span className="menu-division">|</span>
                    <li><NavLink className="session-btns signup-session-btn" to="/signup">signup</NavLink></li>
                    <li><NavLink className="home-session-btn hvr-radial-out" exact to="/"><i class="fas fa-bars"></i></NavLink></li>
                  </ul>
                </nav>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
                  <Route path="/signup" component={SignUp} />
                </div>
              </div>
            </div>
          </HashRouter>
        );
      }
    }
  }
}

export default observer(App);
