  render() {

    // =-------------Use the router instead!!!

    if (UserStore.loading) {
      return (
        <div>
          <div className="App">
            <div className="container">
              loading, please wait...
            </div>
          </div>
        </div>
      )
    } else {

      // === USER IS LOGGED IN===
      if (UserStore.isLoggedIn) {
        return (
          <HashRouter>
            <div className="App">
              <div>
                <Main />
                <nav className="nav-bar">
                  <h1 className="site-name">Culture Club</h1>
                  <img className="logo" src="boat.png" alt="" />
                  <ul>
                    <NavLink className="home-session-btn" exact to="/"><i class="fas fa-bars"></i></NavLink>
                    <LogoutButton
                      onClick={() => this.doLogout()}
                    />
                  </ul>
                </nav>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
                  <Route path="/signup" component={SignUp} />
                </div>
              </div>
            </div>
          </HashRouter>
        )

        // === USER IS NOT LOGGED IN===
      } else {
        return (
          <HashRouter>
            <div className="App">

              <div>
                <Main />
                <nav className="nav-bar">
                  <h1 className="site-name">Culture Club</h1>
                  <img className="logo" src="boat.png" alt="" />
                  <ul>
                    <NavLink className="home-session-btn" exact to="/"><i class="fas fa-bars"></i></NavLink>
                    <li><NavLink className="session-btns login-session-btn" to="/login">login</NavLink></li>
                    <span className="menu-division">|</span>
                    <li><NavLink className="session-btns signup-session-btn" to="/signup">signup</NavLink></li>
                  </ul>
                </nav>
                <div className="content">
                  <Route exact path="/" component={Home} />
                  <Route path="/login" component={Login} />
                  <Route path="/signup" component={SignUp} />
                </div>
              </div>
            </div>
          </HashRouter>
        );
      }
    }
  }
}

export default observer(App);
