import React, { Component } from "react";
import Email from '../signup/Email'
import LoginPassword from './LoginPassword'
import UserStore from "../../stores/UserStore";
import "./Login.css";

class LoginForm extends Component {

  state = {
    email: '',
    password: '',
    buttonDisabled: false,
    isToggleOn: true
  }

  handleToggle = () => {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
  };
  handlePassword = e => {
    this.setState({
      password: e.target.value
    })
  }
  handleEmail = e => {
    this.setState({
      email: e.target.value
    })
  }
  handleSubmit = (e) => {
    e.preventDefault()
  }

  async doLogin() {
    if (!this.state.email) {
      return;
    }
    if (!this.state.password) {
      return;
    }
    this.setState({
      buttonDisabled: true,
    });
    try {
      let res = await fetch("login", {
        method: "post",
        headers: {
          Accept: "application/json",
          "Content-type": "application/json",
        },
        body: JSON.stringify({
          email: this.state.email,
          password: this.state.password,
        }),
      });

      let result = await res.json();

      // ===LOGIN SUCCESSFUL===
      if (result && result.success) {
        UserStore.isLoggedIn = true;
        UserStore.email = result.email;
        console.log(UserStore.email)

        // ===LOGIN FAILED===
      } else if (result && result.success === false) {
        this.resetForm();
        alert(result.msg);
      }
    } catch (e) {
      console.log(e);
      this.resetForm();
    }
  }

  resetForm() {
    this.setState({
      email: "",
      password: "",
      buttonDisabled: false,
    });
  }

  render() {
    const { email, password, buttonDisabled, isToggleOn } = this.state
    return (
      <div className="login-form">
        <form onSubmit={this.handleSubmit}>
          <h2>Log in</h2>
          <Email name="email" email={email} onChange={this.handleEmail} />
          <LoginPassword name="password" handleToggle={this.handleToggle} isToggleOn={isToggleOn} password={password} onChange={this.handlePassword} />
          <button className="login-btn" disabled={buttonDisabled}>Login</button>
        </form>
      </div >
    );
  }
}

export default LoginForm;
