import React from 'react';
import './Email.css'

function Email(props) {
    const styles = {
        required: {
            color: 'red'
        },
        ok: {
            color: 'green'
        }
    };
    return (
        < div className="email-container" >
            <input
                validemail={props.validemail}
                onChange={props.onChange}
                value={props.email}
                placeholder="Email"
                type="Email"
            />
            {props.validemail === 'required'
                ? <p style={styles.required}>Required</p>
                : <p style={styles.ok}>OK</p>}

        </div>
    );
}

export default Email;


