  //         console.log(response.data);
  //       })
  //       .catch(function (error) {
  //         console.log(error);
  //       });
  //   };

  render() {
    console.log(this.state.data);
    return (
      <div>
        <h2>Events</h2>
        <button onClick={() => this.fetchData()}>Check for Events</button>
      </div>
    );
  }
}

export default Events;

