import React, { Component } from "react";
import axios from "axios";
import "./Events.css";
import { Route, NavLink, HashRouter } from "react-router-dom";

class Events extends Component {
  state = {
    data: [],
  };

  //   async fetchData() {
  //     try {
  //       let res = await fetch("getEvents", {
  //         method: "get",
  //       });

  //       let result = await res.json();
  //       this.setState({
  //         data: [result._embedded.events, ...this.state.ingredients],
  //       });
  //     } catch (e) {
  //       console.log(e);
  //     }
  //   }

  async componentDidMount() {
    const response = await fetch(
      `https://app.ticketmaster.com/discovery/v2/events.json?city=melbourne&page=0&size=20&apikey=ARzROEpcmT85JLMu1NVGUCSSgS8LQDGd`
    );
    const json = await response.json();
    this.setState({
      data: this.getUnique(json._embedded.events, "name"),
    });
  }

  //   async fetchData() {
  //     axios
  //       .get(
  //         `https://app.ticketmaster.com/discovery/v2/events.json?city=melbourne&page=0&size=40&apikey=ARzROEpcmT85JLMu1NVGUCSSgS8LQDGd`
  //       )
  //       .then((res) =>
  //         this.setState({
  //           data: this.getUnique(res.data._embedded.events, "name"),
  //         })
  //       );
  //   }

  renderEvents = (e, idx) => {
    return (
      <div className="event" key={idx}>
        <div className="container-event-img">
          <img className="event-img" src={e.images[0].url} alt="" />
        </div>
        <div className="event-info">
          <p className="date-format">
            {`${this.convertDate(e.dates.start.localDate)} ${this.convertTime(
              e.dates.start.localTime
            )}`}
          </p>
          <h3>{e.name}</h3>
          <p>{e._embedded.venues[0].name}</p>
        </div>
      </div>
    );
  };

  convertTime = (time) => {
    let h = time.slice(0, 2);
    let m = time.slice(3, 5);
    let newTime = h > 12 ? h - 12 + ":" + m + " PM" : h + ":" + m + " AM";
    return newTime;
  };

  convertDate = (date) => {
    let newDate = new Date(date);
    let dayOfTheWeek = newDate.toDateString().split(" ").slice(0, 1);
    let dayAndMonth = newDate.toDateString().split(" ").slice(1, 3).join(" ");
    let year = newDate.toDateString().split(" ").slice(-1);
    return `${dayOfTheWeek}, ${dayAndMonth}, ${year}`;
  };

  getUnique = (array, name) => {
    const unique = array
      .map((e) => e[name])
      .map((e, i, final) => final.indexOf(e) === i && i)
      .filter((e) => array[e])
      .map((e) => array[e]);
    return unique;
  };

  render() {
    const { data } = this.state;
    if (data.length !== 0) {
      return (
        <div className="events">
          <h2>Events</h2>
          <section>{data.map(this.renderEvents)}</section>
          {/* <button onClick={() => this.fetchData()}>Check for Events</button> */}
        </div>
      );
    } else {
      return (
        <div>
          <h2>Events</h2>
          {/* <button onClick={() => this.fetchData()}>Check for Events</button> */}
        </div>
      );
    }
  }
}

export default Events;
