import React, { Component } from 'react';
import Password from './Password'
import Email from './Email'
import './SignupForm.css'

class SignupForm extends Component {
    state = {
        email: '',
        password: '',
        validEmail: false,
        isToggleOn: true
    }
    handleToggle = () => {
        this.setState(state => ({
            isToggleOn: !state.isToggleOn
        }));
    };
    handlePassword = e => {
        this.setState({
            password: e.target.value
        })
    }
    handleEmail = e => {
        this.setState({
            email: e.target.value
        })
        const regex = /\S+@\S+\.\S+/;
        if (regex.test(e.target.value)) {
            this.setState({
                validEmail: true
            })
        }
    }
    handleSubmit = (e) => {
        e.preventDefault()
    }
    render() {
        const { email, password, validEmail, isToggleOn } = this.state

        return (
            <div className="sign-up-form">
                <form onSubmit={this.handleSubmit}>
                    <h1>Sign up</h1>
                    <Email name="email" email={email} onChange={this.handleEmail} />
                    {(!validEmail)
                        ? <p className="required">Required</p>
                        : <p className="valid-rmail">OK</p>}
                    <Password name="password" handleToggle={this.handleToggle} isToggleOn={isToggleOn} password={password} onChange={this.handlePassword} />
                </form>
            </div>
        );
    }
}

export default SignupForm;



