import React, { Component } from 'react';
import Password from './Password'
import Email from './Email'
import './SignupForm.css'

class SignupForm extends Component {
    state = {
        email: '',
        password: '',
        isToggleOn: true
    }
    handleToggle = () => {
        this.setState(state => ({
            isToggleOn: !state.isToggleOn
        }));
    };
    handlePassword = e => {
        this.setState({
            password: e.target.value
        })
    }
    handleEmail = e => {
        this.setState({
            email: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault()
    }
    render() {
        const { email, password, isToggleOn } = this.state
        return (
            <div>
                <div className="sign-up-form">
                    <form onSubmit={this.handleSubmit}>
                        <h1>Sign up</h1>
                        <Email name="email" email={email} onChange={this.handleEmail} />
                        <Password name="password" handleToggle={this.handleToggle} isToggleOn={isToggleOn} password={password} onChange={this.handlePassword} />
                    </form>

                </div>
                <img className="image-form" src="https://images.unsplash.com/photo-1464820453369-31d2c0b651af?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1000&q=80" alt="" />
            </div>
        );
    }
}

export default SignupForm;



